import React, { useState, useContext } from 'react'
import axios from 'axios'
import { AuthenticatedUser } from "../contexts/AuthenticatedUser.js"
const Register = () => {
    const [user, setUser] = useState({ username: '', password: '', success: false });
    const { authUser, setAuthUser } = useContext(AuthenticatedUser)
    const handleSubmit = e => {
        e.preventDefault();

        const userData = {
            username: user.username,
            password: user.password
        }

        console.log(userData)
        axios.post('http://localhost:5000/users/add', userData)
            .then(res => setUser({ success: true }))
            .catch(err => {
                alert("Username is already taken!")
                setUser({ username: '', password: '', success: false })
            });

    }

    return (
        !user.success ?
            <div align="center" className="mt-5">
                <form onSubmit={handleSubmit}>
                    <div className="form-group">
                        <label>Username: </label>
                        <input type="text"
                            required
                            className="form-control w-25"
                            value={user.username}
                            onChange={(e) => { setUser({ username: e.target.value, password: user.password }) }}
                        />
                    </div>
                    <div className="form-group">
                        <label>Password: </label>
                        <input type="password"
                            required
                            className="form-control w-25"
                            value={user.password}
                            onChange={(e) => { setUser({ username: user.username, password: e.target.value }) }}
                        />
                    </div>
                    <div className="form-group">
                        <input type="submit" value="Register" className="btn btn-primary" />
                    </div>
                </form>
            </div>
            :
            <div class="jumbotron">
                <h1 class="display-4">You are registered!</h1>
                <p class="lead">You have successfully logged into your account!</p>
                <hr class="my-4" />
                <p>If you want to start listening to music press the button below!</p>
                <a class="btn btn-primary btn-lg" href="#" role="button">Music library</a>
            </div>
    )
}
export default Register
