import React, { useState, useEffect } from 'react'
import axios from 'axios'

const ListOfUsers = () => {
    const [users, setUsers] = useState({ listOfUsers: [] })
    useEffect(() => {
        axios.get('http://localhost:5000/users')
            .then(res => {
                setUsers({ listOfUsers: res.data })
            })
            .catch(err => console.log(err));
    })
    const handleAdmin = (user) => {
        console.log(user)
        axios.post('http://localhost:5000/users/update', user)
            .then(res => {
            })
            .catch(err => console.log(err));
    }
    const handleDelete = (user) => {
        console.log(user._id)
        axios.delete('http://localhost:5000/users/delete/' + user._id)
            .then(res => {
            })
            .catch(err => console.log(err));
    }
    const handlePassword = () => {

    }
    return (
        <div>
            <table class="table">
                <thead class="thead-dark">
                    <tr>
                        <th scope="col">#</th>
                        <th scope="col">Username</th>
                        <th scope="col">Password</th>
                        <th></th>
                        <th scope="col">Admin</th>
                        <th scope="col"></th>
                    </tr>
                </thead>
                <tbody>{
                    users.listOfUsers.map((user, key) => (
                        <tr key={key}>
                            <th scope="row">{key}</th>
                            <td>{user.username}</td>
                            <td><input type="text"
                                minLength="6"
                                className="form-control w-25"
                                placeholder={user.password}
                                onChange={(e) => { user.password = e.target.value }}
                            /></td>
                            <td><button type="button" className="btn btn-secondary btn-sm " onClick={() => { handlePassword(user) }}>Change password</button></td>
                            <td><button type="button" className="btn btn-secondary btn-sm " onClick={() => { handleAdmin(user) }}>{String(user.admin)}</button></td>
                            <td><button type="button" className="btn btn-secondary btn-sm " onClick={() => { handleDelete(user) }}>Delete user</button></td>
                        </tr>
                    ))}


                </tbody>
            </table>
        </div>
    )
}
export default ListOfUsers
