import React, { useRef, useEffect, useState } from "react";
import Spinner from 'react-bootstrap/Spinner'

const Sidebar = ({ musicList, handleNewArtist, setMyMusic, setCanUseArrows, setCurrentArtistPlaylist }) => {
    const [artists, setArtists] = useState([])
    const [loaded, setLoaded] = useState(false)

    useEffect(() => {
        let temp = []
        musicList.music.map(({ artist }, key) => {
            temp = [...temp, artist]
            setArtists(temp)
            if (key === musicList.music.length - 1) {
                setLoaded(true)
            }
        })
    }, [musicList])

    const handleMyMusic = () => {
        setMyMusic(true)
        setCanUseArrows(false)
        setCurrentArtistPlaylist("favorite")
    }
    return (

        <div className="sidebar">
            {!loaded ?
                <Spinner animation="grow" variant="success" className="spinner" />
                :
                <div>
                    <ul className="nav flex-column border-bottom border-secondary pb-3">
                        <li className="nav-item">
                            <button type="button" className="btn btn-outline-secondary mt-3 ml-4" onClick={() => { handleMyMusic() }}>My Music</button>
                        </li>
                    </ul>
                    <ul className="nav flex-column mt-3 ml-4">
                        {[...new Set(artists)].map((artist, key) => (
                            <li className="nav-item" key={key} onClick={() => { handleNewArtist(artist) }}>
                                <a className="">{artist}</a>
                            </li>
                        ))
                        }
                    </ul>
                </div>
            }
        </div>
    )
}

export default Sidebar;
