import React, { useState, useContext } from "react";
import { AuthenticatedUser } from "../contexts/AuthenticatedUser.js"

const Music = () => {
    const { authUser, setAuthUser } = useContext(AuthenticatedUser)
    return (
        <div>
            {authUser != '' ?
                <div class="card-body">
                    <h5 class="card-title">You are not logged in!</h5>
                    <p class="card-text">In order to browse and listen to music you need to either login to your account or create a new one.</p>
                    <a href="#" class="btn btn-primary mr-2 ">Login</a>
                    <a href="#" class="btn btn-primary ">Register</a>
                </div>
                :
                <div class="card-body">
                    <h5 class="card-title">Special title treatment</h5>
                    <p class="card-text">With supporting text below as a natural lead-in to additional content.</p>
                    <a href="#" class="btn btn-primary">Go somewhere</a>
                </div>
            }
        </div>
    )
}

export default Music;
