import React, { useState, useContext, useEffect, useRef } from "react";
import { AuthenticatedUser } from "../contexts/AuthenticatedUser.js"
import { Link } from 'react-router-dom'
import axios from 'axios'
import Player from "./Player.js";
import Sidebar from "./Sidebar.js";

const Music = () => {
    const [musicList, setMusicList] = useState({ music: [] })
    const [src, setSrc] = useState('')
    const [isPlaying, setIsPlaying] = useState({ playing: false })
    const [duration, setDuration] = useState()
    const [currentTime, setCurrentTime] = useState()
    const [currentId, setCurrentId] = useState()

    const { authUser, setAuthUser } = useContext(AuthenticatedUser)

    const player = useRef(null)

    useEffect(() => {
        axios.get('http://localhost:5000/music/')
            .then(res => {
                setMusicList({ music: res.data })
                console.log(res.data)

            })
            .catch(err => console.log(err));
    }, [])
    useEffect(() => {
        if (src !== '') {
            player.current.load()
            player.current.play()
        }

    }, [src])
    const getDuration = function (url, next) {
        var _player = new Audio(url);
        _player.addEventListener("durationchange", function (e) {
            if (this.duration != Infinity) {
                var duration = this.duration
                _player.remove();
                next(duration);
            };
        }, false);
        _player.load();
        _player.currentTime = 24 * 60 * 60;
        _player.volume = 0;
        _player.play();
    };
    const handleTitleClick = (file, key) => {
        setSrc('http://localhost:5000/music/' + file)
        setIsPlaying({ playing: true })
        setCurrentId(key)
        getDuration('http://localhost:5000/music/' + file, function (duration) {
            console.log(src)
            setDuration(duration)
        });
    }
    const handlePlay = (file, key) => {
        if (src === '' && file === undefined) {

        }
        else if ((src === 'http://localhost:5000/music/' + file) || file === undefined) {
            setIsPlaying({ playing: true })
            player.current.play()
        }
        else {
            setSrc('http://localhost:5000/music/' + file)
            setIsPlaying({ playing: true })
            setCurrentId(key)
            getDuration('http://localhost:5000/music/' + file, function (duration) {
                setDuration(duration)
            });
        }
    }
    const handlePause = () => {
        setIsPlaying({ playing: false })
        player.current.pause()
    }
    const handleProgress = () => {
        setCurrentTime(player.current.currentTime)
    }
    const handleEnd = (e) => {
        console.log('ended')
        setSrc('')
        setIsPlaying({ playing: false })
        setDuration('')
        setCurrentTime('')
        player.current.pause()
        player.current.load()
    }
    return (
        <div>
            {authUser.username != '' ?
                <div className="cont">
                    <Sidebar />
                    <div className="music-container">
                        <div className="top-view">
                            <a className="number">#</a>
                            <a className="title">Title</a>
                            <a className="author">Artist</a>
                        </div>
                        {musicList.music.map(({ file, title, artist, duration }, key) => (
                            <div className="middle-view" key={key}>
                                <a className="number">{key}</a>
                                <a onClick={() => { handleTitleClick(file, key) }} className="title">{title}</a>
                                <a className="author">{artist}</a>
                                {!((src === 'http://localhost:5000/music/' + file) && (isPlaying.playing)) ?
                                    <i onClick={() => { handlePlay(file, key) }} className="far fa-play-circle play-button-list fa-2x"></i> :
                                    <i onClick={() => { handlePause() }} className="far fa-pause-circle fa-2x play-button-list"></i>
                                }
                            </div>
                        ))
                        }
                    </div>
                    <Player isPlaying={isPlaying} handlePlay={() => { handlePlay() }} handlePause={() => { handlePause() }} player={player} src={src} setSrc={setSrc} duration={duration} currentTime={currentTime} musicList={musicList} currentId={currentId} setCurrentId={setCurrentId} getDuration={getDuration} setDuration={setDuration} setIsPlaying={setIsPlaying} />
                    <audio ref={player} onTimeUpdate={() => handleProgress()} onEnded={() => { handleEnd() }}>
                        <source src={src} />
                    </audio>
                </div>

                :
                <div className="card-body">
                    <h5 className="card-title">You are not logged in!</h5>
                    <p className="card-text">In order to browse and listen to music you need to either login to your account or create a new one.</p>
                    <Link to="/login" className="btn btn-primary mr-3 ">Login</Link>
                    <Link to="/register" className="btn btn-primary ">Register</Link>
                </div>
            }
        </div>
    )
}

export default Music;
