import React, { useState, useContext, useEffect, useRef } from "react";
import { AuthenticatedUser } from "../contexts/AuthenticatedUser.js"
import { Link } from 'react-router-dom'
import axios from 'axios'
import Player from "./Player.js";

const Music = () => {
    const [musicList, setMusicList] = useState({ music: [] })
    const [src, setSrc] = useState({ src: '' })
    const { authUser, setAuthUser } = useContext(AuthenticatedUser)
    const player = useRef(null)

    useEffect(() => {
        axios.get('http://localhost:5000/music/')
            .then(res => {
                setMusicList({ music: res.data })

            })
            .catch(err => console.log(err));
    }, [])

    const handlePlay = (file) => {
        setSrc({ src: 'http://localhost:5000/music/' + file })
        player.current.play()

        var playPromise = player.current.play();

        // In browsers that don’t yet support this functionality,
        // playPromise won’t be defined.
        if (playPromise !== undefined) {
            playPromise.then(function () {
                // Automatic playback started!
                console.log("!!!")
            }).catch(function (error) {
                // Automatic playback failed.
                // Show a UI element to let the user manually start playback.
                console.log(error)
            });
        }
    }

    return (
        <div>
            {authUser.username != '' ?
                <div className="cont">
                    <div className="music-container">
                        <div className="top-view">
                            <a className="number">#</a>
                            <a className="title">Title</a>
                            <a className="author">Author</a>
                        </div>
                        {musicList.music.map(({ file }, key) => (
                            <div className="middle-view" key={key}>
                                <a className="number">{key}</a>
                                <a onClick={() => { handlePlay(file) }} className="title">{file}</a>
                                <a className="author">Author</a>
                                <i className="far fa-play-circle play-button-list fa-2x"></i>
                            </div>
                        ))
                        }
                    </div>
                    <Player />
                    <audio ref={player}>
                        <source src={src.src} />
                    </audio>
                </div>

                :
                <div className="card-body">
                    <h5 className="card-title">You are not logged in!</h5>
                    <p className="card-text">In order to browse and listen to music you need to either login to your account or create a new one.</p>
                    <Link to="/login" className="btn btn-primary mr-3 ">Login</Link>
                    <Link to="/register" className="btn btn-primary ">Register</Link>
                </div>
            }
        </div>
    )
}

export default Music;
