import React, { useRef, useEffect, useState } from "react";
const Player = ({ isPlaying, handlePlay, handlePause, player, src, setSrc, duration, currentTime, musicList, currentId, setCurrentId, getDuration, setDuration, setIsPlaying }) => {
    const progress = useRef(null)
    const progressBar = useRef(null)
    const [time, setTime] = useState('')
    const [dur, setDur] = useState('')
    const [volume, setVolume] = useState(50)
    const [currentSong, setCurrentSong] = useState()

    const handleProgressBar = (e) => {
        let x = (e.pageX - progressBar.current.getBoundingClientRect().left) / 4
        let newTime = x * duration.rawDuration / 100
        player.current.currentTime = newTime
    }
    const handleLeftButton = () => {
        if (currentId > 0) {
            setSrc('http://localhost:5000/music/' + musicList.music[currentId - 1].file)
            setCurrentId(currentId - 1)
            setDuration({ duration: musicList.music[currentId - 1].duration, rawDuration: musicList.music[currentId - 1].rawDuration })
            setIsPlaying({ playing: true })
        }

    }
    const handleRightButton = () => {
        if (currentId < musicList.music.length - 1) {
            setSrc('http://localhost:5000/music/' + musicList.music[currentId + 1].file)
            setCurrentId(currentId + 1)
            getDuration('http://localhost:5000/music/' + musicList.music[currentId + 1].file, function (duration) {
                console.log(src)
                setDuration(duration)
            });
            setIsPlaying({ playing: true })
        }


    }
    const handleVolume = (e) => {
        setVolume(e.target.value)
        player.current.volume = volume / 100
    }
    return (

        <footer className="py-4 text-white-50 fixed-bottom player">
            <i className="fas fa-angle-double-left left-button fa-2x" onClick={() => { handleLeftButton() }}></i>
            {!isPlaying.playing ?

                <i onClick={() => { handlePlay() }} className="far fa-play-circle play-button-list fa-3x play-button"></i> :
                <i onClick={() => { handlePause() }} className="far fa-pause-circle fa-3x play-button"></i>
            }
            <i className="fas fa-angle-double-right right-button fa-2x" onClick={() => { handleRightButton() }}></i>
            <div className="current-song">
                <a>{currentSong}</a>
            </div>
            <div className="volume-slider">
                <i className="fas fa-volume-up volume-icon fa-lg"></i>
                <input type="range" min="1" max="100" value={volume} className="slider" id="myRange" onChange={(e) => { handleVolume(e) }} />
            </div>
            <div className="progr-bar ">
                <div className="currentTime">
                    {time}
                </div>
                <div ref={progress} className="duration-bar" onClick={(e) => { handleProgressBar(e) }}>

                    <div ref={progressBar} style={{ width: currentTime / duration * 100 + '%' }} className="currentTime-bar" ></div>
                </div>
                <div className="duration">
                    {dur}
                </div>
            </div>

        </footer>
    )
}

export default Player;
