import React, { useRef, useEffect } from "react";

const Player = ({ isPlaying, handlePlay, handlePause, player, src }) => {
    const progress = useRef(null)
    const progressBar = useRef(null)
    const handleProgress = () => {
        var duration = player.current.duration
        let currentTime = player.current.currentTime
        var getDuration = function (url, next) {
            var _player = new Audio(url);
            _player.addEventListener("durationchange", function (e) {
                if (this.duration != Infinity) {
                    var duration = this.duration
                    _player.remove();
                    next(duration);
                };
            }, false);
            _player.load();
            _player.currentTime = 24 * 60 * 60;
            _player.volume = 0;
            _player.play();
        };

        getDuration(src, function (duration) {
            console.log(duration)
        });
        //console.log(duration, currentTime, src)
    }
    player.addEventListener("ontimeupdate", () => { handleProgress() })
    return (

        <footer className="py-4 text-white-50 fixed-bottom player">
            <i className="fas fa-angle-double-left left-button fa-2x"></i>
            {console.log(isPlaying)}
            {!isPlaying.playing ?

                <i onClick={() => { handlePlay() }} className="far fa-play-circle play-button-list fa-3x play-button"></i> :
                <i onClick={() => { handlePause() }} className="far fa-pause-circle fa-3x play-button"></i>
            }
            <i className="fas fa-angle-double-right right-button fa-2x"></i>
            <div ref={progress} className="progress">
                <div ref={progressBar} className="progress-bar mt-5" role="progressbar" aria-valuenow="100" aria-valuemin="0" aria-valuemax="100"></div>
            </div>
        </footer>
    )
}

export default Player;
