import React, { useState, useContext } from 'react'
import axios from 'axios'
import { AuthenticatedUser } from "../contexts/AuthenticatedUser.js"
const Register = () => {
    const [user, setUser] = useState({ username: '', password: '' });
    const { authUser, setAuthUser } = useContext(AuthenticatedUser)
    const handleSubmit = e => {
        e.preventDefault();

        const userData = {
            username: user.username,
            password: user.password
        }

        console.log(userData)
        axios.post('http://localhost:5000/users/add', userData)
            .then(res => window.location = "/login")
            .catch(err => alert("Username is already taken!"));

    }

    return (
        <div align="center" className="mt-10">
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label>Username: </label>
                    <input type="text"
                        required
                        className="form-control w-25"
                        value={user.username}
                        onChange={(e) => { setUser({ username: e.target.value, password: user.password }) }}
                    />
                </div>
                <div className="form-group">
                    <label>Password: </label>
                    <input type="password"
                        required
                        className="form-control w-25"
                        value={user.password}
                        onChange={(e) => { setUser({ username: user.username, password: e.target.value }) }}
                    />
                </div>
                <div className="form-group">
                    <input type="submit" value="Register" className="btn btn-primary" />
                </div>
            </form>
        </div>
    )
}
export default Register
