import React, { useRef, useEffect, useState } from "react";
const Sidebar = ({ musicList, handleNewArtist }) => {

    const [artists, setArtists] = useState([])

    useEffect(() => {
        let temp = []
        musicList.map(({ artist }, key) => {
            temp = [...temp, artist]
            setArtists(temp)
        })
    }, [musicList])


    return (
        <div className="sidebar">
            <ul className="nav flex-column border-bottom border-secondary pb-3">
                <li className="nav-item">
                    <button type="button" className="btn btn-outline-secondary mt-3 ml-4">My Music</button>
                </li>
            </ul>
            <ul className="nav flex-column mt-3 ml-4">
                {[...new Set(artists)].map((artist, key) => (
                    <li className="nav-item" key={key} onClick={() => { handleNewArtist(artist) }}>
                        <a className="">{artist}</a>
                    </li>
                ))
                }
            </ul>

        </div>
    )
}

export default Sidebar;
