import React, { useRef, useEffect } from "react";

const Player = ({ isPlaying, handlePlay, handlePause, player, src, duration, currentTime }) => {
    const progress = useRef(null)
    const progressBar = useRef(null)

    useEffect(() => {
        console.log(currentTime, duration)
        console.log(progressBar.current.offsetWidth)
    }, [])

    return (

        <footer className="py-4 text-white-50 fixed-bottom player">
            <i className="fas fa-angle-double-left left-button fa-2x"></i>
            {!isPlaying.playing ?

                <i onClick={() => { handlePlay() }} className="far fa-play-circle play-button-list fa-3x play-button"></i> :
                <i onClick={() => { handlePause() }} className="far fa-pause-circle fa-3x play-button"></i>
            }
            <i className="fas fa-angle-double-right right-button fa-2x"></i>
            <div ref={progress} className="progress">
                <div ref={progressBar} className="progress-bar" role="progressbar" aria-valuenow="100" aria-valuemin="0" aria-valuemax="100"></div>
            </div>
        </footer>
    )
}

export default Player;
