import React, { useRef, useEffect, useState } from "react";

const Player = ({ isPlaying, handlePlay, handlePause, player, src, duration, currentTime }) => {
    const progress = useRef(null)
    const progressBar = useRef(null)
    const [time, setTime] = useState('')
    useEffect(() => {
        if (currentTime != NaN) {
            let minutes = Math.floor(currentTime / 60);
            let seconds = Math.floor(currentTime - minutes * 60);
            if (seconds < 10) {
                seconds = '0' + seconds
            }
            if (minutes < 10) {
                minutes = '0' + minutes
            }
            //console.log(currentTime, duration)
            // console.log(progressBar.current.offsetWidth)
            setTime(minutes + ':' + seconds)
            console.log(time)
        }
    })

    return (

        <footer className="py-4 text-white-50 fixed-bottom player">
            <i className="fas fa-angle-double-left left-button fa-2x"></i>
            {!isPlaying.playing ?

                <i onClick={() => { handlePlay() }} className="far fa-play-circle play-button-list fa-3x play-button"></i> :
                <i onClick={() => { handlePause() }} className="far fa-pause-circle fa-3x play-button"></i>
            }
            <i className="fas fa-angle-double-right right-button fa-2x"></i>
            <div className="progr-bar">
                <a className="currentTime">{time}</a>
                <div ref={progress} className="duration-bar">
                    <div ref={progressBar} style={{ width: currentTime / duration * 100 + '%' }} className="currentTime-bar"></div>
                </div>
                <a className="duration"></a>
            </div>
        </footer>
    )
}

export default Player;
