import React from 'react'
import axios from 'axios'
import { AuthenticatedUser } from "../contexts/AuthenticatedUser.js"
const CreateUser =()=>{
    state = {
        username: "",
        password: ""
    }
    //static contextType = AuthenticatedUser
    onChangeUsername = e => {
        this.setState({
            username: e.target.value
        })
    }
    onChangePassword = e => {
        this.setState({
            password: e.target.value
        })
    }
    onSubmit = e => {
        e.preventDefault();
        // const {authenticatedUser}=this.context
        // authenticatedUser(this.state.username)
        const user = {
            username: this.state.username,
            password: this.state.password
        }
        console.log(user)
        axios.post('http://localhost:5000/users/add', user)
            .then(res => console.log(res.data))
            .catch(err => alert("Username is already taken!"));
            console.log(this.context)
        //window.location = "/"
    }
        
        return (
            <div>
                <h3>Create New User</h3>
                <form onSubmit={this.onSubmit}>
                    <div className="form-group">
                        <label>Username: </label>
                        <input type="text"
                            required
                            className="form-control"
                            value={this.state.username}
                            onChange={this.onChangeUsername}
                        />
                    </div>
                    <div className="form-group">
                        <label>Password: </label>
                        <input type="password"
                            required
                            className="form-control"
                            value={this.state.password}
                            onChange={this.onChangePassword}
                        />
                    </div>
                    <div className="form-group">
                        <input type="submit" value="Create User" className="btn btn-primary" />
                    </div>
                </form>
            </div>
        )
}
export default CreateUser
