import React, { useState, useContext } from 'react'
import axios from 'axios'
import { AuthenticatedUser } from "../contexts/AuthenticatedUser.js"

const Login = () => {
    const [user, setUser] = useState({ username: '', password: '' });
    const { authUser, setAuthUser } = useContext(AuthenticatedUser)
    const handleSubmit = e => {
        e.preventDefault();

        const userData = {
            username: user.username,
            password: user.password
        }

        console.log(userData)
        axios.post('http://localhost:5000/users/login', userData)
            .then(res => {
                console.log(res.data[0].password)
                if (userData.password == res.data[0].password) {
                    alert("Logged in!")
                    setAuthUser(user.username)
                    console.log(authUser)
                }
                else {
                    alert("Wrong username or password!")
                }
            })
            .catch(err => alert(err));
        setUser({ username: '', password: '' })
    }


    return (
        <div>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label>Username: </label>
                    <input type="text"
                        required
                        className="form-control"
                        value={user.username}
                        onChange={(e) => { setUser({ username: e.target.value, password: user.password }) }}
                    />
                </div>
                <div className="form-group">
                    <label>Password: </label>
                    <input type="password"
                        required
                        className="form-control"
                        value={user.password}
                        onChange={(e) => { setUser({ username: user.username, password: e.target.value }) }}
                    />
                </div>
                <div className="form-group">
                    <input type="submit" value="Login" className="btn btn-primary" />
                </div>
            </form>
        </div>
    )
}
export default Login
