import React, { useState, useContext } from 'react'
import axios from 'axios'
import { AuthenticatedUser } from "../contexts/AuthenticatedUser.js"
const CreateUser = () => {
    const [user, setUser] = useState({ username: '', password: '' });
    const {authUser,setAuthUser}=useContext(AuthenticatedUser)
    const onChangeUsername = e => {
        setUser({ username: e.target.value, password: user.password })
    }
    
    const onChangePassword = e => {
        setUser({ username: user.username, password: e.target.value })
    }
    
    const handleSubmit = e => {
        e.preventDefault();

        const userData = {
            username: user.username,
            password: user.password
        }
        console.log(userData)
        axios.post('http://localhost:5000/users/add', userData)
            .then(res => console.log(res.data))
            .catch(err => alert("Username is already taken!"));
        //window.location = "/"
        setUser({username:'',password:''})
        setAuthUser(user.username)
        console.log(authUser)
    }

    
    return (
        <div>
            <h3>Create New User</h3>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label>Username: </label>
                    <input type="text"
                        required
                        className="form-control"
                        value={user.username}
                        onChange={onChangeUsername}
                    />
                </div>
                <div className="form-group">
                    <label>Password: </label>
                    <input type="password"
                        required
                        className="form-control"
                        value={user.password}
                        onChange={onChangePassword}
                    />
                </div>
                <div className="form-group">
                    <input type="submit" value="Create User" className="btn btn-primary" />
                </div>
            </form>
        </div>
    )
}
export default CreateUser
