import React, { useRef, useEffect, useState } from "react";
import Spinner from 'react-bootstrap/Spinner'
import ListGroup from 'react-bootstrap/ListGroup'


const Sidebar = ({ musicList, handleNewArtist, setMyMusic, setCurrentArtistPlaylist }) => {
    const [artists, setArtists] = useState([])
    const [loaded, setLoaded] = useState(false)

    useEffect(() => {
        let temp = []
        musicList.music.map(({ artist }, key) => {
            temp = [...temp, artist]
            setArtists(temp)
            if (key === musicList.music.length - 1) {
                setLoaded(true)
            }
        })
    }, [musicList])

    const handleMyMusic = () => {
        setMyMusic(true)
        setCurrentArtistPlaylist("favorite")
    }
    return (

        <div className="sidebar">
            {!loaded ?
                <Spinner animation="grow" variant="success" className="spinner" />
                :
                <div>
                    <ul className="nav flex-column border-bottom border-secondary pb-3">
                        <li className="nav-item">
                            <button type="button" className="btn btn-outline-secondary mt-3 ml-4" onClick={() => { handleMyMusic() }}>My Music</button>
                        </li>
                    </ul>
                    <ListGroup variant="flush">
                        {[...new Set(artists)].map((artist, key) => (
                            <ListGroup.Item key={key} onClick={() => { handleNewArtist(artist) }}> Dapibus ac facilisis in</ListGroup.Item>
                        ))
                        }
                    </ListGroup>
                </div>
            }
        </div>
    )
}

export default Sidebar;
