import React, { useRef, useEffect, useState } from "react";
const MusicList = ({ musicList, handleTitleClick, handlePlay, handlePause, isPlaying, src, currentArtist }) => {
    const [currentPlaylist, setCurrentPlaylist] = useState([])

    useEffect(() => {
        musicList.music.filter(song => song.artist === currentArtist).map(filteredSong => (
            console.log(filteredSong)
        ))


    }, [currentArtist])

    return (
        <div className="music-container">
            <div className="top-view">
                <div className="number">#</div>
                <div className="title">Title</div>
                <div className="author">Artist</div>
                <div className="time">
                    <i className="far fa-clock fa-lg"></i>
                </div>
            </div>
            {musicList.music.filter(song => song.artist === currentArtist).map(({ file, title, artist, duration, rawDuration }, key) => (

                <div className="middle-view" key={key}>
                    <div className="number">{key}</div>
                    <div className="title" onClick={() => { handleTitleClick(file, key, duration, rawDuration) }}>{title}</div>
                    <div className="author">{artist}</div>
                    <div className="time">{duration}</div>
                    <div className="play-button-list">
                        {!((src === 'http://localhost:5000/music/' + file) && (isPlaying.playing)) ?
                            <i onClick={() => { handlePlay(file, key, duration, rawDuration) }} className="far fa-play-circle fa-2x"></i> :
                            <i onClick={() => { handlePause() }} className="far fa-pause-circle fa-2x "></i>
                        }
                    </div>
                </div>
            ))
            }
        </div>
    )
}

export default MusicList;
