import React, { createContext, Component } from 'react'

export const AuthenticatedUser =createContext();

const AuthenticatedUserProvider =()=> {
    const [user, setUser] =useState({username:'test'})
    const authenticatedUser=(username)=>{
        setUser({username})
    }
        return (
            <AuthenticatedUser.Provider value={{...this.state, authenticatedUser: this.authenticatedUser }}>
                {this.props.children}
            </AuthenticatedUser.Provider>
          );
}
 
export default AuthenticatedUserProvider;
