import React, { createContext, useState } from 'react'

export const AuthenticatedUser = createContext();

const AuthenticatedUserProvider = (props) => {
    const [authUser, setAuthUser] = useState({ username: 'test' })
    const setAuthenticatedUser = (username) => {
        setAuthUser({ username })
    }
    return (
        <AuthenticatedUser.Provider value={{ authUser, setAuthUser:setAuthenticatedUser}}>
            {props.children}
        </AuthenticatedUser.Provider>
    );
}

export default AuthenticatedUserProvider;
