import React, { useState, useContext, useEffect, useRef } from "react";
import { AuthenticatedUser } from "../contexts/AuthenticatedUser.js"
import { Link } from 'react-router-dom'
import axios from 'axios'
import Player from "./Player.js";
import Sidebar from "./Sidebar.js";
import MusicList from "./MusicList.js";

const Music = () => {
    const [musicList, setMusicList] = useState({ music: [] })
    const [src, setSrc] = useState('')
    const [isPlaying, setIsPlaying] = useState({ playing: false })
    const [duration, setDuration] = useState({ duration: '', rawDuration: '' })
    const [currentTime, setCurrentTime] = useState()
    const [currentId, setCurrentId] = useState()
    const [currentArtist, setCurrentArtist] = useState('')
    const [currentPlaylist, setCurrentPlaylist] = useState([])
    const [favoriteMusic, setFavoriteMusic] = useState([])
    const [myMusic, setMyMusic] = useState(false)
    const [canUseArrows, setCanUseArrows] = useState(false)
    const { authUser, setAuthUser } = useContext(AuthenticatedUser)

    const player = useRef(null)

    useEffect(() => {
        axios.get('http://localhost:5000/music/')
            .then(res => {
                setMusicList({ music: res.data })
            })
            .catch(err => console.log(err));
        axios.post('http://localhost:5000/music/favorite', authUser)
            .then(res => {
                let temp = []
                res.data.map(({ title }) => {
                    temp = [...temp, title]
                    setFavoriteMusic(temp)
                })
            })
            .catch(err => console.log(err));
    }, [])
    useEffect(() => {
        if (src !== '') {
            player.current.load()
            player.current.play()
        }

    }, [src])

    const handleTitleClick = (file, key, duration, rawDuration) => {
        setSrc('http://localhost:5000/music/' + file)
        setIsPlaying({ playing: true })
        setCurrentId(key)
        setDuration({ duration: duration, rawDuration: rawDuration })
    }
    const handlePlay = (file, key, duration, rawDuration) => {
        if (src === '' && file === undefined) { }
        else if ((src === 'http://localhost:5000/music/' + file) || file === undefined) {
            setIsPlaying({ playing: true })
            player.current.play()
            setCanUseArrows(true)
        }
        else {
            setSrc('http://localhost:5000/music/' + file)
            setIsPlaying({ playing: true })
            setCurrentId(key)
            setDuration({ duration: duration, rawDuration: rawDuration })
            setCanUseArrows(true)
        }
    }
    const handlePause = () => {
        setIsPlaying({ playing: false })
        player.current.pause()
    }
    const handleProgress = () => {
        setCurrentTime(player.current.currentTime)
    }
    const handleEnd = (e) => {
        console.log('ended')
        setSrc('')
        setIsPlaying({ playing: false })
        setDuration({ duration: '', rawDuration: '' })
        player.current.pause()
        player.current.load()
        setCurrentTime('')
    }
    const handleNewArtist = (artist) => {
        setCurrentArtist(artist)
        let temp = musicList.music.filter(song => song.artist === artist)
        setCurrentPlaylist(temp)
        setMyMusic(false)
        setCanUseArrows(false)
    }
    return (
        <div>
            {authUser.username != '' ?
                <div className="cont">
                    <Sidebar musicList={musicList} handleNewArtist={handleNewArtist} setMyMusic={setMyMusic} setCurrentArtist={setCurrentArtist} />
                    <MusicList musicList={musicList} handleTitleClick={handleTitleClick} handlePlay={handlePlay} handlePause={handlePause} isPlaying={isPlaying} src={src} currentArtist={currentArtist} authUser={authUser} favoriteMusic={favoriteMusic} setFavoriteMusic={setFavoriteMusic} currentPlaylist={currentPlaylist} myMusic={myMusic} setCurrentPlaylist={setCurrentPlaylist} />
                    <Player isPlaying={isPlaying} handlePlay={() => { handlePlay() }} handlePause={() => { handlePause() }} player={player} src={src} setSrc={setSrc} duration={duration} currentTime={currentTime} musicList={currentPlaylist} currentId={currentId} setCurrentId={setCurrentId} setDuration={setDuration} setIsPlaying={setIsPlaying} currentArtist={currentArtist} myMusic={myMusic} />
                    <audio ref={player} onTimeUpdate={() => handleProgress()} onEnded={() => { handleEnd() }}>
                        <source src={src} />
                    </audio>
                </div>

                :
                <div className="card-body">
                    <h5 className="card-title">You are not logged in!</h5>
                    <p className="card-text">In order to browse and listen to music you need to either login to your account or create a new one.</p>
                    <Link to="/login" className="btn btn-primary mr-3 ">Login</Link>
                    <Link to="/register" className="btn btn-primary ">Register</Link>
                </div>
            }
        </div>
    )
}

export default Music;
